from pprint import pp as pp

def match(s):
  '''
  >>> match('211/2212')
  5

  >>> match('22/11')
  1

  >>> match('/1211/2///2111/2222/11')
  7

  >>> match('//2/2/211')
  1

  >>> match('111/222')
  7

  >>> match('')
  2001
  '''
  # least length is 1, S must have at least one slash

  l = s.split('/')
  m = min(max([i.count('1') for i in l]), max([i.count('2') for i in l]))
  for i in range(m, 0, -1):
    ss = '1' * i + '/' + '2' * i
    if ss in s:
      return i * 2 + 1
  return 1


def main():
    import sys
    lines = sys.stdin.read().split('\n')
    # pp(lines)
    ret = match(lines[1])
    print(ret)


if __name__ == "__main__":
    main()
